sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	// Respond to the updateNote event
	$rootScope.$on('updateNote', function(event,data) {
		console.log('Listener caught update to NoteID: ' + $rootScope.noteID);

		c.server.get({
			action: 'updateNote',
			noteID: $rootScope.noteID
		}).then(function(r) { // 'r' is the response from the server?
			console.log('Got a response from the server for NoteID: ' + r.data.note.sys_id);
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		})
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	// Create the notes property on the data object which will contain an array of objects
	data.notes = [];

	// Query the database to find all Note table records for the currently logged in user
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user',gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on'); // Retrieve the most recently created user record
	noteGR.query();

	// For each note record returned
	while(noteGR.next()){
		// Create an empty noteObj object
		var noteObj = {};

		// Get the display values for the number, title and sys_id fields, put these in the noteObj object
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');

		// Get the first 20 characters of the description field and add to the noteObj object
		noteObj.note = noteGR.getValue('note').slice(0,20);

		// Push the noteObj into the notes array
		data.notes.push(noteObj);

	}
	if ( input && input.noteID ) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if( note.get( input.noteID)) {
			if( input.action == 'updateNote') {
				data.note = {};
				$sp.getRecordValues(data.note, note, 'title, note, sys_id');
			} else if ( input.action == 'updateNote') {
				note.title = input.noteTitle;
				note.note = input.noteBody;
				note.update();
			}
		}
	} 
})();
